'use strict';

const FOOD = {
    'ðŸŒ¯': { price: 150, require: {} },
    'ðŸ¥™': { price: 180, require: {} },
    'ðŸŒ­': { price: 80, require: {} },
    'ðŸ¥“': { price: 50, require: {} },
    'ðŸŸ': { price: 40, require: {} },
    'ðŸ¥¤': { price: 30, require: {} },
    'â˜•': { price: 40, require: {} },
};
function get_default_order() {
    return {
        'ðŸŒ¯': 0,
        'ðŸ¥™': 0,
        'ðŸŒ­': 0,
        'ðŸ¥“': 0,
        'ðŸŸ': 0,
        'ðŸ¥¤': 0,
        'â˜•': 0,
    };
}
function get_order_total_price(order) {
    return Object
        .keys(order)
        .reduce((sum, key) => sum + order[key] * FOOD[key].price, 0);
}

window.onload = () => {
    let order = get_default_order();
    const order_el = document.getElementById("order");
    const total_price_el = document.getElementById("total_price");
    const reset_order_el = document.getElementById("reset_order");
    reset_order_el.onclick = () => {
        location.reload();
    };
    order_el.onclick = (e) => {
        const button = e.target;
        button.classList.add("pure-button-active");
        const item = button.getAttribute("data-item");
        order[item] += 1;
        button.innerText = `${order[item]}${item}`;
        total_price_el.value = `${get_order_total_price(order)}`;
    };
};
